import React, { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = () => {
const [enteredTitle, setEnteredTitle] = useState('');
const [enteredAmount, setEnteredAmount] = useState('');
const [enteredDate, setEnteredDate] = useState('');

    const titleChangeHandler = (title)=>{
        setEnteredTitle(title.target.value)
        
    }
    const amountChangeHandler = (amount)=>{
        setEnteredAmount(amount.target.value)

    }
    const dateChangeHandler = (date)=>{
        setEnteredDate(date.target.value)
    }

    const submitHandler = (event)=>{
        event.preventDefault();
const expenseData = {
    title:enteredTitle,
    amount:enteredAmount,
    date:new Date(enteredDate),
}

    }
  return (
    <form onSubmit={submitHandler} >
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}  />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-1-1" max="2022-12-31" onChange={dateChangeHandler} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add expense</button>
      </div>
    </form>
  );
};
export default ExpenseForm;
